import React from "react";
import BackgroundImage from "../../assets/images/BackgroundRegisterScreen.png";
import DefaultInput from "../../components/DefaultInput";
import { Formik, Form } from "formik";
import { useNavigate } from "react-router-dom";
import * as Yup from "yup";
import { Container, SubArea, ErrorMessage } from "./styles";

const loginSchema = Yup.object().shape({
  name: Yup.string().required("Nome é um campo obrigatório"),
  surname: Yup.string().required("Sobrenome é um campo obrigatório"),
  email: Yup.string().required("Email é um campo obrigatório").email(),
  password: Yup.string()
    .required("Password é um campo obrigatório")
    .min(6, "Password em formato inválido"),
});

const Register = () => {
  return (
    <Container>
      <SubArea width="710px" backgroundImage={BackgroundImage}></SubArea>
      <SubArea width="730px">
        <Formik
          validationSchema={loginSchema}
          initialValues={{ name: "", surname: "", email: "", password: "" }}
          onSubmit={async ({ name, surname, email, password }) => {
            try {
            } catch (err) {}
          }}
        >
          {({ values, errors, touched, handleSubmit, handleChange }) => (
            <Form noValidate onSubmit={handleSubmit}>
              <Title>Crie uma Conta</Title>
              <Resume>Por-favor forneça seus dados</Resume>

              <DefaultInput
                customMarginTop="4px"
                type="name"
                name="name"
                onChange={handleChange}
                value={values.name}
                placeHolder="Digite seu nome"
                id="name"
              />

              <ErrorMessage>
                {errors.name != null && touched.name != null && errors.name}
              </ErrorMessage>
              <DefaultInput
                customMarginTop="4px"
                type="surname"
                name="surname"
                onChange={handleChange}
                value={values.surname}
                placeHolder="Digite seu sobrenome"
                id="surname"
              />

              <ErrorMessage>
                {errors.surname != null &&
                  touched.surname != null &&
                  errors.surname}
              </ErrorMessage>

              <DefaultInput
                customMarginTop="4px"
                type="email"
                name="email"
                onChange={handleChange}
                value={values.email}
                placeHolder="Digite seu email@..."
                id="email"
              />

              <ErrorMessage>
                {errors.email != null && touched.email != null && errors.email}
              </ErrorMessage>
              <DefaultInput
                customMarginTop="4px"
                type="password"
                name="password"
                onChange={handleChange}
                value={values.password}
                placeHolder="Digite sua senha..."
                id="password"
              />

              <ErrorMessage>
                {errors.password != null &&
                  touched.password != null &&
                  errors.password}
              </ErrorMessage>

              <DefaultInput
                customBackgroundColor="#04D361"
                type="submit"
                marginTop="16px"
                opacity={0.8}
                onClick={handleSubmit}
              >
                Criar conta
              </DefaultInput>
            </Form>
          )}
        </Formik>
      </SubArea>
    </Container>
  );
};

export default Register;
