/* eslint-disable @typescript-eslint/member-delimiter-style */
import styled from "styled-components";

interface ICustomButton {
  customBackgroundColor?: string;
  customColor?: string;
  width?: string;
  height?: string;
  marginTop?: string;
  opacity?: number;
  customBorder?: string;
  fontFamily?: string;
}

export const ButtonStyles = styled.button<ICustomButton>`
  width: ${(props) => props.width ?? "480px"};
  height: ${(props) => props.width ?? "60px"};
  top: 563px;
  left: 830px;
  gap: 10px;
  border-radius: 5px;
  opacity: 0px;
  background: #6e74f8;
  font-family: Inter;
  font-size: 16px;
  font-weight: 700;
  line-height: 19.36px;
  letter-spacing: 0.1em;
  text-align: center;
  color: #ffffff;
  padding: 0;

  &:hover {
    opacity: ${(props) => props.opacity};
    background-color: #556073;
    cursor: pointer;
  }
`;
